/* Generates a rv that is exponentially distributed Exp[1], or normally distributed N(0, 1)*/#include <math.h>extern double genrand_real1();double expdev(), gasdev();double expdev(){	double dum;		do{		dum = genrand_real1();	} while (dum == 0.0);	return -log(1-dum);}double gasdev(){	static int iset = 0;	static double gset;	double fac, rsq, v1, v2;		if (iset == 0){		do{			v1 = 2.0*genrand_real1()-1.0;			v2 = 2.0*genrand_real1()-1.0;			rsq = v1*v1+v2*v2;		} while (rsq >=1.0 || rsq == 0.0);		fac = sqrt(-2.0*log(rsq)/rsq);		gset = v1*fac;		iset = 1;		return v2*fac;	} else{		iset = 0;		return gset;	}}